import { __decorate } from "tslib";
import { Directive, ElementRef, HostBinding, HostListener, Input, Renderer2 } from '@angular/core';
let BetterHighlightDirective = class BetterHighlightDirective {
    constructor(elRef, renderer) {
        this.elRef = elRef;
        this.renderer = renderer;
        this.defaultColor = 'transparent';
        this.highlightColor = 'blue';
    }
    ngOnInit() {
        this.backgroundColor = this.defaultColor;
        //this.renderer.setStyle(this.elRef.nativeElement, 'background-color', 'blue')
    }
    mouseover(eventData) {
        //this.renderer.setStyle(this.elRef.nativeElement, 'background-color', 'blue')
        this.backgroundColor = this.highlightColor;
    }
    mouseleave(eventData) {
        this.backgroundColor = this.defaultColor;
        //this.renderer.setStyle(this.elRef.nativeElement, 'background-color', 'transparent')
    }
};
BetterHighlightDirective.ctorParameters = () => [
    { type: ElementRef },
    { type: Renderer2 }
];
BetterHighlightDirective.propDecorators = {
    defaultColor: [{ type: Input }],
    highlightColor: [{ type: Input }],
    backgroundColor: [{ type: HostBinding, args: ['style.backgroundColor',] }],
    mouseover: [{ type: HostListener, args: ['mouseenter',] }],
    mouseleave: [{ type: HostListener, args: ['mouseleave',] }]
};
BetterHighlightDirective = __decorate([
    Directive({
        selector: '[appBetterHighlight]'
    })
], BetterHighlightDirective);
export { BetterHighlightDirective };
